mapper.insert:
SELECT IFNULL(MAX(USR_ID), 80000000) + 1 AS usrId FROM USER_INFO WHERE USR_ID > 80000000
/*ShopJoinDao.insert*/ INSERT INTO USER_INFO( USR_ID ,USR_PW ,USR_NM ,STORE_ID ,EMAIL ,MOBILE ,JOIN_USR_ID ,JOIN_ROUTE ,JOIN_DT ,USR_TP ,USR_LV ,USE_YN ,PROPOSER ,SMS_NOTI_AGRE_AT ,SMS_AD_AGRE_AT ,EMAIL_AD_AGRE_AT ,PW_LAST_MOD_DT ,CREATE_DT ,CREATE_ID
) VALUES ( IFNULL(#storeId#,#usrId#) ,SHA1(#shaPw#) ,#usrNm# ,#storeId# ,#email# ,#mobileNo# ,#joinUsrId# ,#joinRoute# ,DATE_FORMAT(now(), '%Y%m%d') ,#usrTp# ,#usrLv# ,#useYN# ,#prmCd# ,#smsRcvAgree# ,#adAgreeYN# ,#adAgreeYN# ,DATE_FORMAT(now(), '%Y%m%d') ,now() ,IFNULL(#storeId#,#usrId#) )
