    getTargetDepth: function(depth) {
        var depthObj = eval('depth' + depth);
        return depthObj
    },
    getTargetName: function(key) {
        for (var x = 1; x < 5; x++) {
            var list = eval('depth' + x);
            for (var prop in list) {
                if (key == list[prop].C_CD) {
                    return list[prop].C_NM;
                    break
                }
            }
        }
    },
    getTargetInfo: function(key) {
        var result = {};
        for (var d4 = 4; d4 > 0; d4--) {
            var list = eval('depth' + d4);
            for (var prop in list) {
                if (key == list[prop].C_CD) {
                    T_CD = list[prop].T_CD;
                    P_CD = list[prop].P_CD;
                    result = list[prop]
                }
            }
        }
        return result
    },
    getTargetTree: function(key) {
        var result = {};
        for (var d4 = 4; d4 > 0; d4--) {
            var list = eval('depth' + d4);
            for (var prop in list) {
                if (key == list[prop].C_CD) {
                    T_CD = list[prop].T_CD;
                    P_CD = list[prop].P_CD;
                    result[d4] = list[prop];
                    for (var d3 = d4; d3 > 0; d3--) {
                        var list = eval('depth' + d3);
                        for (var prop in list) {
                            if (P_CD == list[prop].C_CD) {
                                T_CD = list[prop].T_CD;
                                P_CD = list[prop].P_CD;
                                result[d3] = list[prop];
                                for (var d2 = d3; d2 > 0; d2--) {
                                    var list = eval('depth' + d2);
                                    for (var prop in list) {
                                        if (P_CD == list[prop].C_CD) {
                                            T_CD = list[prop].T_CD;
                                            P_CD = list[prop].P_CD;
                                            result[d2] = list[prop];
                                            for (var d1 = d2; d1 > 0; d1--) {
                                                var list = eval('depth' + d1);
                                                for (var prop in list) {
                                                    if (P_CD == list[prop].C_CD) {
                                                        T_CD = list[prop].T_CD;
                                                        P_CD = list[prop].P_CD;
                                                        result[d1] = list[prop];
                                                        break
                                                    }
                                                }
                                            }
                                            break
                                        }
                                    }
                                }
                                break
                            }
                        }
                    }
                    break
                }
            }
        }
        return result
    },
    getChildrenList: function(key, tar) {
        var result = [];
        var list = eval('depth' + tar);
        for (var prop in list) {
            if (key == list[prop].P_CD) {
                result.push(list[prop])
            }
        }
        return result
    },
